package com.wafflestudio.seminar.survey.apiimport com.wafflestudio.seminar.survey.database.OsRepositoryimport com.wafflestudio.seminar.survey.domain.OperatingSystemimport com.wafflestudio.seminar.survey.domain.SurveyResponseimport com.wafflestudio.seminar.survey.service.SeminarServiceimport org.springframework.beans.factory.annotation.Autowiredimport org.springframework.web.bind.annotation.GetMappingimport org.springframework.web.bind.annotation.RequestMappingimport org.springframework.web.bind.annotation.RestController@RestControllerclass SeminarController(val seminarService: SeminarService) {    @RequestMapping("/api/v1")    @GetMapping("/surveyall")    fun Listall(): List<SurveyResponse>{        return seminarService.getAllSurvey()    }    @GetMapping("/survey/{ID}")    fun getSurveyByID(ID: Long): SurveyResponse?{        return seminarService.getSurveyByID(ID)    }    @GetMapping("/OS/{ID}")    fun getOSByID(ID: Long): OperatingSystem?{        return seminarService.getOSByID(ID)    }    fun getOSByName(name: String): OperatingSystem?{        return seminarService.getOSByName(name)    }}