package com.wafflestudio.seminar.survey.databaseimport com.wafflestudio.seminar.survey.domain.SurveyResponseimport org.springframework.stereotype.Component@Componentclass SurveyResponseRepositoryImpl(val memoryDB: MemoryDB):SurveyResponseRepository {    override fun findAll(): List<SurveyResponse>{        return memoryDB.getSurveyResponses()    }    override fun findById(id: Long): SurveyResponse?{        val sr = memoryDB.getSurveyResponses()        for(i in sr){            if(i.id == id){                return i            }        }        return null    }}