package com.wafflestudio.seminar.survey.serviceimport com.wafflestudio.seminar.survey.database.OsRepositoryimport com.wafflestudio.seminar.survey.database.SurveyResponseRepositoryimport com.wafflestudio.seminar.survey.domain.OperatingSystemimport com.wafflestudio.seminar.survey.domain.SurveyResponseimport org.springframework.stereotype.Service@Serviceclass SeminarService(private val osrepository: OsRepository, private val surveyresponserepository: SurveyResponseRepository, ) {    fun getAllSurvey() : List<SurveyResponse>{        return surveyresponserepository.findAll()    }        fun getSurveyByID(id: Long): SurveyResponse?{        return surveyresponserepository.findById(id)    }        fun getOSByID(id: Long): OperatingSystem?{        return osrepository.findById(id)    }        fun getOSByName(name : String?): OperatingSystem?{        return osrepository.findByName(name)    }}